using MVC_AE2.Models;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Web;
using System.Web.Mvc;

namespace MVC_AE2.Controllers
{
    public class MoviesController : Controller
    {
        private static List<Movie> listMovies = new List<Movie>()
        {
            new Movie(){MovieId = 1, Name = "American Psycho", Rating = 4.8f, DownloadURL = @"https://encrypted-tbn3.gstatic.com/images?q=tbn:ANd9GcSJkc5QBoCplF1PKcJyX4Ag0tfDuoXBbolfkYDfCYGGaeYNpXtc", ImageURL = @"https://encrypted-tbn3.gstatic.com/images?q=tbn:ANd9GcSJkc5QBoCplF1PKcJyX4Ag0tfDuoXBbolfkYDfCYGGaeYNpXtc"},
            new Movie(){MovieId = 2, Name = "Fight club", Rating = 5f, DownloadURL = @"data:image/jpeg;base64,", ImageURL = @"data:image/jpeg;base64,"},
            new Movie(){MovieId = 3, Name = "Taxi Driver", Rating = 4.7f, DownloadURL= @"data:image/jpeg;base64,", ImageURL = @"data:image/jpeg;base64,"}
        };

        private static List<Client> listClients = new List<Client>()
        {
            new Client(){ Name = "Jane P.", MembershipCard = "1234", Address = "bul. Random Randomizer 69", Telephone = "070223305", Age = 20, IsSubscribed = false},
            new Client(){ Name = "Johny B.", MembershipCard = "5678", Address = "bul. Weed club 420", Telephone = "077777777", Age = 32, IsSubscribed = true},
            new Client(){ Name = "Pink P.", MembershipCard = "9100", Address = "bul. Children Generator 1", Telephone = "070007007", Age = 15, IsSubscribed = true}
        };

        //Actions for Movies

        public ActionResult GetAllMovies()
        {
            return View(listMovies);
        }

        public ActionResult ShowMovie(int id)
        {
            var movie = listMovies.ElementAt(id);
            MovieRentals model = new MovieRentals();
            model.movie = movie;
            model.customers = listClients;
            return View(model);
        }

        public ActionResult NewMovie()
        {
            Movie model = new Movie();
            return View(model);
        }

        [HttpPost]
        public ActionResult InsertNewMovie(Movie model)
        {
            if (!ModelState.IsValid)
            {
                return View("NewMovie", model);
            }

            listMovies.Add(model);
            return View("GetAllMovies", listMovies);
        }

        public ActionResult EditMovie(int id)
        {
            var model = listMovies.ElementAt(id);
            model.MovieId = id;
            return View(model);
        }

        [HttpPost]
        public ActionResult EditMovie(Movie model)
        {
            if (!ModelState.IsValid)
            {
                return View("EditMovie", model);
            }

            var editedMovie = listMovies.ElementAt(model.MovieId);
            editedMovie.MovieId = model.MovieId;
            editedMovie.Name = model.Name;
            editedMovie.Rating = model.Rating;
            editedMovie.DownloadURL = model.DownloadURL;
            editedMovie.ImageURL = model.ImageURL;

            return View("GetAllMovies", listMovies);
        }

        public ActionResult DeleteMovie(int id)
        {
            listMovies.RemoveAt(id);
            return View("GetAllMovies", listMovies);
        }

        //Actions for Clients

        public ActionResult ShowClient(int id)
        {
            Client model = listClients.ElementAt(id);
            return View(model);
        }

        public ActionResult NewClient()
        {
            Client model = new Client();
            return View(model);
        }

        [HttpPost]
        public ActionResult InsertNewClient(Client model)
        {
            if (!ModelState.IsValid)
            {
                return View("NewClient", model);
            }

            listClients.Add(model);
            return View("GetAllMovies", listMovies);
        }
    }
}